import React, { Component } from 'react';
import { InputGroup, InputGroupAddon, Input, Button, Form } from 'reactstrap';
import AppHeader from '../common/AppHeader';


class LoginPage extends Component {
    constructor(props) {
        super(props);
        this.state = { username: '',
        password: '',
        error: false, };

        this.handleChange = this.handleChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
      }
      onSubmit(e) {
        e.preventDefault();

        const { username, password } = this.state;
        const { history } = this.props;

        this.setState({ error: false });

        if (!(username === 'didik' && password === '12345')) {
            return this.setState({ error: true });
        }

        store.set('loggedIn', true);
        history.push('/login');
    }

    handleChange(e) {
        const target = e.target;
        const value = target.value;
        const name = target.name;
        this.setState({ [name]: value });
    }


    render() {
        return (
            <div>
                <AppHeader />
                <h1>LOGIN</h1>
                <div>
                    <Form onSubmit={this.handleSubmit}>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">@</InputGroupAddon>
                            <Input placeholder="username" type="text" value={this.state.value} onChange={this.handleChange} />
                        </InputGroup>
                        <br />
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">Pass</InputGroupAddon>
                            <Input placeholder="password " type="password" value={this.state.value} onChange={this.handleChange} />
                        </InputGroup>
                        <br />
                        {error && <Alert color="danger">
                        Salah password
                    </Alert>
                    }
                    <input
                        label="Username"
                        name="username"
                        onChange={this.handleChange}
                    />
                    <input
                        label="Password"
                        type="password"
                        name="password"
                        onChange={this.handleChange}
                    />
                        <Button type="submit" value="Submit">Login</Button>
                    </Form>
                </div>

            </div>
        );
    }
}


export default LoginPage;
