import React, { Component } from 'react';
import { InputGroup, InputGroupAddon, Input, Button, Form , Alert} from 'reactstrap';
import AppHeader from '../common/AppHeader';
import store from 'store';
import { Redirect } from 'react-router-dom';
import isLoggedIn from '../helpers/is_logged_in';



class LoginPage extends Component {
    constructor(props) {
        super(props);
        this.state = { username: '',
        password: '',
        error: false,
        dataUser:{}
     };

        this.handleChange = this.handleChange.bind(this);
        this.onSignIn = this.onSignIn.bind(this);
      }
      onSignIn(e) {
        e.preventDefault();

        const { username, password } = this.state;
       const { history } = this.props;

        this.setState({ error: false });

        if (!(username === 'naufal' && password === 'waduh')) {
            return this.setState({ error: true });
        }

        store.set('loggedIn', true);
        history.push('/');
    }

    handleChange(e) {
        const target = e.target;
        const value = target.value;
        const name = target.name;
        this.setState({ [name]: value });
    }


    render() {

        const { error } = this.state;
        if (isLoggedIn()) {
            return <Redirect to="/" />;
        }
        return (
            <div>
                <AppHeader />
                <h1>LOGIN</h1>
                <div>
                    <Form error={error} onSignIn={this.onSignIn}>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">@</InputGroupAddon>
                            <Input placeholder="username" type="text" value={this.state.value} onChange={this.handleChange} />
                        </InputGroup>
                        <br />
                        <InputGroup>
                        {error && <Alert color="danger">
                        Salah password
                    </Alert>
                    }
                            <InputGroupAddon addonType="prepend">Pass</InputGroupAddon>
                            <Input placeholder="password " type="password" value={this.state.value} onChange={this.handleChange} />
                        </InputGroup>
                        <br />


                        <Button type="submit" value="Submit">Login</Button>
                    </Form>
                </div>

            </div>
        );
    }
}


export default LoginPage;
