import React from 'react';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  Container,
  DropdownItem } from 'reactstrap';
import {Link, withRouter} from 'react-router-dom'
import Avatar from 'react-avatar';
import store from 'store'
import isLoggedIn from '../helpers/is_logged_in';
import axios from 'axios';

 class AppHeader extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false,
   items: {}
    };
    this.logOut = this.logOut.bind(this)

  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }
  componentWillMount(isLoggedIn)   {

      if (isLoggedIn())
    axios.get(`https://historic-acadia-33199.herokuapp.com/api/user/${localStorage.getItem('USER_ID')}`
    )
        .then(res => {
            this.setState({
              items: res.data.data


            })
          });

      } 



logOut() {
  store.remove('loggedIn');
  localStorage.removeItem('JWT_TOKEN')
  localStorage.removeItem('USER_ID')
  this.props.history.push('/login')

}
  render() {
    let loginicon
    let logInOut

    const items = this.state.items

    if (isLoggedIn())  {
      logInOut = <NavLink><Link onClick={this.logOut} className='text-white' to='/login' >Logout</Link></NavLink> 

      loginicon = <DropdownToggle nav caret ><Avatar size="25" round={true} src={items.user_foto}>
                </Avatar> {items.username}</DropdownToggle>


  } else {logInOut = <NavLink  ><Link className='text-white' to='/login' >Login</Link></NavLink>}
    return (
      <div>
        <Navbar color="danger" light expand="md">
            <Container >
          <NavbarBrand ><Link to='/'className="text-white"><img height='40px' src='https://monggovest.herokuapp.com/static/img/Logo-White@2x.39103d4.png' alt=''/></Link></NavbarBrand>
          <NavbarToggler onClick={this.toggle} />
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav className="ml-auto" navbar>
              <NavItem>
                <NavLink ><Link to='/investasi'className="text-white">Investasi</Link></NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
              </NavItem>
              <UncontrolledDropdown nav inNavbar>
                  {loginicon}
                <DropdownMenu right>
                  <DropdownItem>
                    Option 1
                  </DropdownItem>
                  <DropdownItem>
                    Option 2
                  </DropdownItem>
                  <DropdownItem divider />
                  <DropdownItem>
                    Reset
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
      {logInOut}
            </Nav>
          </Collapse>
          </Container >
        </Navbar>
      </div>
    );
  }
}
export default withRouter(AppHeader)
