import React from 'react';
import { FormGroup, Label, Col, Input, Button, Form, NavLink } from 'reactstrap';
import AppHeader from '../common/AppHeader';
import store from 'store';
import { Redirect, Link } from 'react-router-dom';
import isLoggedIn from '../helpers/is_logged_in';
import axios from 'axios'

axios.defaults.baseURL = 'https://historic-acadia-33199.herokuapp.com'


class LoginPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: "",
            check: false,
            hidden: true
        };

        this.handleChange = this.handleChange.bind(this);

        this.handleChecked = this.handleChecked.bind(this);

        this.loginBos = this.loginBos.bind(this)
    }

    handleChange(e) {
        let name = e.target.name;
        let value = e.target.value;
        let data = {};
        data[name] = value;
        this.setState(data);
    }

    handleChecked = () => {
        this.setState({
            check: !this.state.check,
            hidden: !this.state.hidden
        });
    }
    loginBos(e) {
        e.preventDefault();
        axios
            .post("/api/user/login", {
                username: this.state.username,
                password: this.state.password
            })


            .then((res) => {
                console.log(res)
                alert(res.data.message);
                localStorage.setItem('JWT_TOKEN', res.data.token)
                localStorage.setItem('USER_ID', res.data._id)
                store.set('loggedIn', true);
                this.props.history.push('/')
            })
            .catch(function (err) {
                //console.log(res.data.message);
                alert("email atau password anda salah", err);
            });
        this.setState({
            username: "",
            password: ""
        })

    }

    render() {


        if (isLoggedIn()) {
           return( <Redirect to="/investasi" />
           )
        } else {
        return (
            <div>
                <AppHeader />
                <h1>LOGIN</h1>
                <div>
                    <Form align="stretch" >
                        <FormGroup>
                            Email:<br />
                            <Input type="text" name="username" value={this.state.username} onChange={this.handleChange} placeholder="Masukkan Email Anda" />
                        </FormGroup>
                        <FormGroup>
                            <Label>Password: </Label>
                            <Input type={this.state.hidden ? "password" : "text"} required="required" name="password" value={this.state.password} placeholder="Kata Sandi Anda" onChange={this.handleChange}
                            />
                            <Label check sm={12}>
                                <Col sm={12}>
                                    <Input type="checkbox" checked={this.state.check} onChange={this.handleChecked} />lihat kata sandi
                </Col>
                            </Label>
                        </FormGroup>

                        <div align="center">
                            <Button onClick={this.loginBos} color="submit" className="btn-primary">MASUK</Button>
                        </div>
                        <div align="center">

                            <NavLink ><Link to='/register' className="text-info">Belum Punya Akun? Skuy SignUp</Link></NavLink>

                        </div>
                    </Form>
                </div>

            </div>
        );
        }
    }
}


export default LoginPage;
