import React /*, {createRef }*/ from 'react';
import store from 'store'
import isLoggedIn from '../helpers/is_logged_in'
import { Label, FormGroup, Input, Container, Button, Form, Col } from 'reactstrap';
import AppHeader from '../common/AppHeader';
//import {useDropzone} from 'react-dropzone'

import { Redirect } from 'react-router-dom'
import axios from "axios";
//const dropzoneref = createRef

axios.defaults.baseURL = 'https://historic-acadia-33199.herokuapp.com'

class SignUp extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            user_foto: '',
            password: '',
            email: '',
            username: '',
            roles: '',
            confirmPassword: '',
            check: false,
            checkI: false


        }
        this.changePassword = this.changePassword.bind(this)
        this.changeUsername = this.changeUsername.bind(this)
        this.changeEmail = this.changeEmail.bind(this)
        this.changeRoles = this.changeRoles.bind(this)
        this.changeConfirmPassword = this.changeConfirmPassword.bind(this)



    }

    changePassword(event) {
        this.setState({
            password: event.target.value
        })
    }
    changeEmail(event) {
        this.setState({
            email: event.target.value
        })
    }
    changeUsername(event) {
        this.setState({
            username: event.target.value
        })
    }
    changeRoles(event) {
        this.setState({
            roles: event.target.value
        })
    }
    changeConfirmPassword(event) {
        this.setState({
            email: event.target.value
        })
    }
    handleCheckedPeternak = () => {
        this.setState({
            check: !this.state.check,
            hidden: !this.state.hidden
        });
    }
    handleCheckedInvestor = () => {
        this.setState({
            checkI: !this.state.checkI,
            hidden: !this.state.hidden
        });
    }
    handleUploadImages = images => {
        // uploads is an array that would hold all the post methods for each image to be uploaded, then we'd use axios.all()
        const uploads = images.map(image => {
            // our formdata
            const formData = new FormData();
            formData.append("file", image);
            formData.append("tags", 'user_foto'); // Add tags for the images - {Array}
            formData.append("upload_preset", "efx7bxvu"); // Replace the preset name with your own
            formData.append("api_key", "149952854421636"); // Replace API key with your own Cloudinary API key
            formData.append("timestamp", (Date.now() / 1000) | 0);
            formData.append("http://res.cloudinary.com/dcnhkq6qt", url);

            // Replace cloudinary upload URL with yours
            return axios.post(
                "https://api.cloudinary.com/v1_1/dcnhkq6qt/image/upload",
                formData,
                { headers: { "X-Requested-With": "XMLHttpRequest" } })
                .then(response => console.log(response.data))
        });

        // We would use axios `.all()` method to perform concurrent image upload to cloudinary.
        axios.all(uploads).then(() => {
            // ... do anything after successful upload. You can setState() or save the data
            console.log('Images have all being uploaded')
        });
    }

    signUp(event) {
        event.preventDefault();
        if (this.state.password === this.state.confirmPassword) {
            axios
                .post("https://historic-acadia-33199.herokuapp.com/api/user", {
                    user_foto: this.state.user_foto,
                    email: this.state.email,
                    password: this.state.password,
                    username: this.state.username,
                    roles: this.state.roles
                })
                .then((response) => {
                    console.log(response, "the response");

                    localStorage.setItem('JWT_TOKEN', response.data.token)
                    store.set('loggedIn', true);
                    localStorage.setItem('USER_ID', response.data.id)
                    this.props.history.push('/');
                    alert('Anda berhasil masuk. Selamat Datang di Monggovest');

                })
                .catch(function (error) {
                    console.log("the error", error);
                    alert("error", error);
                });
            this.setState({
                email: "",
                password: ""
            })

        } else {
            alert('Pastikan password anda benar!')
        }

    }

    render() {

        console.log(isLoggedIn())
        if (isLoggedIn()) {
            return (
                <Redirect to='/' />
            )
        }
        console.log('images, this.state.image.url')
        let imageUpload 
        if (this.state.image.url === undefined) 
         {
            imageUpload =
            // <FormGroup row ref={dropzoneref}
            //         onDrop={this.handleUploadImages}
            //     >
            //         <div {...getRootProps()}>
            //             <input {...getInputProps()} />
            //             <img style={{ width: 100 }} alt=''></img>
            //         </div>      </FormGroup>

                       <FormGroup row>
                                <Col sm='3' style={{ textAlign: 'left' }}><Label for="user_foto">Profile Photo</Label></Col>
                                <Col sm='9'><Input type="file" name="file"  /></Col>
                            </FormGroup>
        }
        else {
            imageUpload = <img style={{ width: 100 }} src= {this.state.image.url} alt=''></img>
        }
        return (
            <div>
                <AppHeader />

                <div>
                    <Container style={{ textAlign: 'center' }}>
                        <h2 style={{ margin: '30px' }}>Sign Up</h2>
                        <Form style={{ width: '500px', margin: 'auto' }}>
{imageUpload}
                            <FormGroup row>
                                <Col sm='3' style={{ textAlign: 'left' }}><Label for='username'>Username: </Label></Col>
                                <Col sm='9'><Input name='username' type='username' onChange={this.changeUsername} placeholder="username" /></Col>
                            </FormGroup>
                            <FormGroup row>
                                <Col sm='3' style={{ textAlign: 'left' }}><Label for='email'>Email: </Label></Col>
                                <Col sm='9'><Input name='email' type='email' onChange={this.changeEmail} placeholder="email" /></Col>
                            </FormGroup>
                            <FormGroup row>
                                <Col sm='3' style={{ textAlign: 'left' }} ><Label for='roles'>Roles: </Label></Col>
                                <Col sm='4'><Input type="checkbox" checked={this.state.check} onChange={this.handleCheckedPeternak} />Peternak</Col>
                                <Col sm='4'><Input type="checkbox" checked={this.state.checkI} onChange={this.handleCheckedInvestor} />Investor</Col>
                            </FormGroup>
                            <FormGroup row>
                                <Col sm='3' style={{ textAlign: 'left' }} ><Label for='password'>Password: </Label></Col>
                                <Col sm='9'><Input name='password' type='password' onChange={this.changePassword} placeholder="password" /></Col>
                            </FormGroup>
                            <FormGroup row>
                                <Col sm='3' style={{ textAlign: 'left' }} ><Label for='ConfirmPassword'>Confirm Password: </Label></Col>
                                <Col sm='9'><Input name='ConfirmPassword' type='ConfirmPassword' onChange={this.changeConfirmPassword} placeholder="confirm password" /></Col>
                            </FormGroup>





                            <Button onClick={this.SignUp}>Sign Up !!!</Button>
                        </Form>

                    </Container>
                </div>
            </div>

        );
    }

}


export default SignUp;
