import React, { Component } from 'react';
import axios from 'axios';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';



class HomeCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      activeIndex: 0,
      items:[

      ]

    };
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.goToIndex = this.goToIndex.bind(this);
    this.onExiting = this.onExiting.bind(this);
    this.onExited = this.onExited.bind(this);
  }

  onExiting() {
    this.animating = true;
  }

  onExited() {
    this.animating = false;
  }

  next() {
    if (this.animating) return;

    const nextIndex = this.state.activeIndex === this.state.items.length - 1 ? 0 : this.state.activeIndex + 1;
    this.setState({ activeIndex: nextIndex });
  }

  previous() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === 0 ? this.state.items.length - 1 : this.state.activeIndex - 1;
    this.setState({ activeIndex: nextIndex });
  }

  goToIndex(newIndex) {
    if (this.animating) return;
    this.setState({ activeIndex: newIndex });
  }

  componentDidMount(){
    axios.get('https://historic-acadia-33199.herokuapp.com/api/user/komoditas'
      )
    .then(res => {
      console.log(res.data, 'ini fetch data');
        this.setState({
          items: res.data
        })
    })
    .catch(err => {
      console.log(err.message);
    })
  }

  render() {
    console.log('ini data', this.state.items)
    const { activeIndex } = this.state;
    const {items} = this.state
    //const {foto, asal, harga, altText} = this.state.items
    let slides;
    // if(items.length !=0){
    //    slides = this.state.items.map((item,index) => {
    //     return (
    //       <CarouselItem
    //         className="custom-tag"
    //         tag="div"
    //         key = {index}
    //         onExiting={this.onExiting}
    //         onExited={this.onExited}
    //       >
    //         <img className="carousel-img" src={item.foto} alt={item.altText}/>
    //         <CarouselCaption className="text-danger" captionText={item.asal} captionHeader={item.harga} />
    //       </CarouselItem>
    //     );
    //   });
    // }


    return (
      <div>
        <style>
          {
            `.custom-tag {
                max-width: 100%;
                height: 500px;
                background: black;
              }`
          }
        </style>
       { items.length !==0 
       ?<Carousel
          activeIndex={activeIndex}
          next={this.next}
          previous={this.previous}
        >
          <CarouselIndicators items={this.state.items} activeIndex={activeIndex} onClickHandler={this.goToIndex} />
          {items.map((item,index) => {
        return (
          <CarouselItem
            className="custom-tag"
            tag="div"
            key = {index}
            onExiting={this.onExiting}
            onExited={this.onExited}
          >
            <img className="carousel-img" src={item.foto} alt={item.altText}/>
            <CarouselCaption className="text-danger" captionText={item.asal} captionHeader={item.harga} />
          </CarouselItem>
        );
      })}
          <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
          <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
        </Carousel>
        :null
       }
      </div>
    );
  }
}


export default HomeCarousel;
