import React from 'react';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  Container,
  DropdownItem } from 'reactstrap';
import {Link, withRouter} from 'react-router-dom'
//import Avatar from 'react-avatar';
import store from 'store'
import isLoggedIn from '../helpers/is_logged_in';
import axios from 'axios';

 class AppHeader extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false,
   items: {}
    };
    this.logOut = this.logOut.bind(this)

  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }
  logOut() {
    store.remove('loggedIn')
    localStorage.removeItem('JWT_TOKEN')
    localStorage.removeItem('USER_ID')
    this.props.history.push('/login')

  }
  componentDidMount()  {if (isLoggedIn())   {
    axios.get(`https://historic-acadia-33199.herokuapp.com/api/user/${localStorage.getItem('USER_ID')}`)
        .then(res => {
            this.setState({
              items: res.data.data


            })
          }).catch(err => {
            console.log(err)
            alert(err)

        })
      } 
    }



  render() {
    let logInOut
    let logincon
    const items = this.state.items
    if (isLoggedIn()) {

        logInOut = <NavLink  ><Link onClick={this.logOut} className='text-white' >Logout</Link></NavLink>

        logincon =
            <UncontrolledDropdown nav inNavbar>
                <DropdownToggle className='text-white' nav caret>
                    <img src={items.user_foto} style={{width:'30px'}} alt=""/>
                </DropdownToggle>
                <DropdownMenu right>
                    <DropdownItem className='text-primary'>
                        {items.username}
                    </DropdownItem>
                    <DropdownItem className='text-primary'>
                        Investasi Saya
                    </DropdownItem>
                    <DropdownItem divider />
                    <DropdownItem className='text-primary'>
                        Setting
                    </DropdownItem>
                </DropdownMenu>
            </UncontrolledDropdown>
    } else {
        logInOut = <NavLink  ><Link className='text-white' to='/login' >Login</Link></NavLink>
    }
    return (
        <div>
            <Navbar color="dark" light expand="md">

                <NavbarBrand className='text-white' href="/"><img style={{ width: "100px" }} src="https://monggovest.herokuapp.com/static/img/Logo-White@2x.39103d4.png" /></NavbarBrand>
                <NavbarToggler onClick={this.toggle} />
                <Collapse isOpen={this.state.isOpen} navbar>
                    <Nav className="ml-auto" navbar>
                        <NavItem>
                            <NavLink href="https://historic-acadia-33199.herokuapp.com/api/komoditas/data"><Link className='text-white' to='/investasi' >Investasi</Link></NavLink>
                        </NavItem>
                        {logincon}

                        <NavItem>
                            {logInOut}
                        </NavItem>
                    </Nav>

                </Collapse>


            </Navbar>

        </div>
    );
}
 }

export default withRouter (AppHeader)
