import React, { Component } from 'react';
import AppHeader from '../common/AppHeader';
import {
    Card, Button, CardImg, CardTitle, Container, Row,
    CardSubtitle, CardBody,
} from 'reactstrap';
import axios from 'axios';
import { Link } from 'react-router-dom'
import AppFooter from '../common/AppFooter';

class KomoditasList extends Component {
    constructor(props) {
        super(props);
        this.state = {

            items: [


            ]

        };
    }
    componentDidMount() {
        axios.get('https://historic-acadia-33199.herokuapp.com/api/komoditas/data')
            .then(res => {
                this.setState({
                    items: res.data.result
                })
            });
    }
    render() {
        console.log('ini data', this.state.items)


        const cards = this.state.items.map((item) => {
            return (
                <div class='col-md-4'>


                    <Card >
                        <CardImg top width="100%" height="200px" src={item.foto} alt="Card image cap" />

                        <CardBody>
                            <CardTitle>{item.nama_ternak}</CardTitle>
                            <CardSubtitle>Harga: {item.harga}</CardSubtitle>
                            <CardSubtitle>Asal: {item.asal}</CardSubtitle>
                            <CardSubtitle>Lot Tersedia: {item.lot_tersedia}</CardSubtitle>

                            <Button color="info"><Link to={`/investasi/${item._id}`}>Invest</Link></Button>

                        </CardBody>


                    </Card>

                </div>
            );
        })
        return (
            <div>
                <AppHeader />

                <Container>
                    <h1 style={{ width: '100px', margin: 'auto', textAlign: 'center' }}>Komoditas Tersedia</h1>
                    <hr />
                    <Row>
                        {cards}
                    </Row>

                </Container>

                <AppFooter />

            </div >
        );
    }
}

export default KomoditasList;
