import axios from 'axios';
import {Link} from 'react-router-dom'
import Detail from './Detail';

class DetailPage extends Component {
    constructor(props) {
        super(props);
        this.state = {

            items: {}




        };
    }
    componentDidMount() {
        axios.get(`http://reduxblog.herokuapp.com/api/posts/${this.props.match.params.id}`
        )
            .then(res => {
               /* this.setState({
