import React from 'react';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  Container,
  DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom'
import axios from 'axios';
import isLoggedIn from '../helpers/is_logged_in';

export default class AppHeader extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false,
     user_foto: '',
     username: '',
     email: ''
    };
  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }
  componentDidMount() {
    axios.get(`https://historic-acadia-33199.herokuapp.com/api/user/${localStorage.getItem('USER_ID')}`
    )
        .then(res => {
            this.setState({
                user_foto: res.user_foto,
                username: res.username,
                email: res.email

            })

            console.log(this.state.username, "OK")
        });
}
  render() {
    let loginicon

    if (isLoggedIn())  {
     loginicon = <DropdownToggle nav caret> <div class="flex items-center h-100 icon"><div class="pa0 mr3 ml0 br-100 ba b--white"><div class="br-100 box-xxl lh-xxl tc fw9 white flex items-center justify-center f-sm sc-gqjmRU eKxvyz" img={user_foto} ></div></div><div class="-userBlockInfo___1W"><span><span data-test="user-block-name" class="lh-xs white f-sm fw5"><span class="w-100 -trunc-wrapper___3a">{email}</span></span></span><span><span class="lh-xs white f-xs " data-test="subtext-block"><span class="w-100 -trunc-wrapper___3a">{username}</span></span></span></div></div></DropdownToggle>
  } else {loginicon=  <DropdownToggle>Options</DropdownToggle>}
    return (
      <div>
        <Navbar color="danger" light expand="md">
            <Container >
          <NavbarBrand ><Link to='/'className="text-white"><img height='40px' src='https://monggovest.herokuapp.com/static/img/Logo-White@2x.39103d4.png' alt=''/></Link></NavbarBrand>
          <NavbarToggler onClick={this.toggle} />
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav className="ml-auto" navbar>
              <NavItem>
                <NavLink ><Link to='/investasi'className="text-white">Investasi</Link></NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
              </NavItem>
              <UncontrolledDropdown nav inNavbar>
                  {loginicon}
                <DropdownMenu right>
                  <DropdownItem>
                    Option 1
                  </DropdownItem>
                  <DropdownItem>
                    Option 2
                  </DropdownItem>
                  <DropdownItem divider />
                  <DropdownItem>
                    Reset
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
              <NavLink><Link to='/login'className="text-white">Login</Link></NavLink>
            </Nav>
          </Collapse>
          </Container >
        </Navbar>
      </div>
    );
  }
}
