import React,{ Component }  from 'react';
import {
    Card,  CardImg, CardTitle, CardText, 
    CardSubtitle, CardBody, Container, Button
} from 'reactstrap';
import axios from 'axios';
import AppHeader from '../common/AppHeader';
import isLoggedIn from '../helpers/is_logged_in';
import { Redirect } from 'react-router-dom';
import Slider from 'react-rangeslider'
import 'react-rangeslider/lib/index.css'
import Detail from './Detail';

class DetailPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            value: 10,
            jumlah_lot: "",
            items: [


            ]



        };
        this.postLot = this.postLot.bind(this)
        this.changeLot = this.changeLot.bind(this)

    }

    handleChangeStart = () => {
        console.log('Change event started')
      };

      handleChange = value => {
        this.setState({
          value: value
        })
      };

      handleChangeComplete = () => {
        console.log('Change event completed')
      };
    changeLot(event) {
      this.setState({
          jumlah_lot: event.target.value
      })
  }
    componentDidMount() {
        axios.get(`https://historic-acadia-33199.herokuapp.com/api/komoditas/data/${this.props.match.params.id}`
        )
            .then(res => {
               this.setState({
                    items: res.data.data
                })
                console.log(res , "sgs")
            });
    }
    postLot(event) {
      event.preventDefault();
      // if (this.state.asal === this.state.confirmasal) {

      axios
          .post(`https://historic-acadia-33199.herokuapp.com/api/user/invest/lot/${this.props.match.params.id}`, 

          {
              jumlah_lot: this.state.jumlah_lot
          },
              {
                  headers: {
                      Authorization: localStorage.getItem('JWT_TOKEN')
                    }

              }

        )
          .then((res) => {
              console.log(res, "the res");
              alert('jumlah lot anda tersimpan');
              this.props.history.push('/')

          })
          .catch(function (error) {
              console.log("the error", error);
              alert("error", error);
          });
  }








    render() 

     {

        if (!isLoggedIn () || localStorage.getItem('Roles') === 'Peternak' ) {
            return (alert("Maaf peternak tidak dapat melakukan investasi") , <Redirect to='/' /> ) 
         } 
        console.log(this.props.match.params.id)
        console.log(this.state.jumlah_lot, "tess")
        const {foto, nama_ternak, harga, lot_tersedia, asal, jumlah_lot} = this.state.items
        const { value } = this.state

        return (

            <div>
<AppHeader />
                <Detail />
                <Container>
                <Card >
                            <CardImg top width="100%" height="500px" src={foto} alt="Card image cap" />

                           <CardBody>
                                <CardTitle>{nama_ternak}</CardTitle>
                                <CardSubtitle>{harga}</CardSubtitle>
                                <CardText>Asal: {asal} <br></br>Lot yang tersedia: {lot_tersedia}</CardText>
                                <CardTitle>Jumlah Lot yang diinginkan: </CardTitle>

                                <div className='slider'>
        <Slider
          min={0}
          max={20}
          value={value}
          onChangeStart={this.handleChangeStart}
          onChange={this.changeLot}
          onChangeComplete={this.handleChangeComplete}
        />
        <div className='value'>{value}</div>
      </div>


  </CardBody>

  <br></br>
  <Button onClick={this.postLot} color='submit' className="btn-primary">Submit Lot</Button>

                        </Card>
                        </Container>

            </div>
         ) ;


    }

}

export default DetailPage;
