import React, { Component } from 'react';
import { FormGroup, Label, Col, Input, Button, Form } from 'reactstrap';
import AppHeader from '../common/AppHeader';
import store from 'store';
import { Redirect } from 'react-router-dom';
import isLoggedIn from '../helpers/is_logged_in';
import axios from 'axios'

axios.defaults.baseUrl = 'https://historic-acadia-33199.herokuapp.com'

class LoginPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: "",
            check: false,
            hidden: true
        };

        this.handleChange = this.handleChange.bind(this);

        this.handleChecked = this.handleChecked.bind(this);

        this.onSubmit = this.onSubmit.bind(this)
    }

    onSubmit(e) {
        e.preventDefault();
        axios
            .post("https://historic-acadia-33199.herokuapp.com/api/user/login", {
                username: this.state.username,
                password: this.state.password
            })
            //.then((response) => {

                if (response.status(201).json) {
                    localStorage.setItem('JWT_TOKEN', response.data.token)
                    store.set('loggedIn', true);
                    alert('Anda berhasil masuk. Selamat Datang di Monggovestplus');
                    const token = localStorage.getItem('JWT_TOKEN')
                    const tokenParts = token.split('.');
                    const encodedPayload = tokenParts[1];
                    const rawPayload = atob(encodedPayload);
                    const user = JSON.parse(rawPayload);
                    localStorage.setItem('USER_ID', user.userId)
                    console.log('userId yang login', token)
                    this.props.toggle()
                    this.props.checkAuth()
                }

            //})
            // .catch(function (error) {
            //     console.log("the error", error);
            //     if (error.response.data.status === 401) {
            //         alert("Akun Anda belum teraktivasi, silahkan cek e-mail yang telah didaftarkan sebelumnya");
            //     } else {
            //         alert("telah terjadi error, mohon hubungi tim kami untuk mendapat bantuan", error.response.status);
            //     }
            // });
        this.setState({
            username: "",
            password: ""
        })


    }

    handleChange(e) {
        let name = e.target.name;
        let value = e.target.value;
        let data = {};
        data[name] = value;
        this.setState(data);
    }

    handleChecked = () => {
        this.setState({
            check: !this.state.check,
            hidden: !this.state.hidden
        });
    }

    render() {


        if (isLoggedIn()) {
            return <Redirect to="/" />;
        }
        return (
            <div>
                <AppHeader />
                <h1>LOGIN</h1>
                <div>
                    <Form align="stretch" onSubmit={this.submit}>
                        <FormGroup>
                            Email:<br />
                            <Input type="text" name="username" value={this.state.username} onChange={this.handleChange} placeholder="Masukkan Email Anda" />
                        </FormGroup>
                        <FormGroup>
                            <Label>Password: </Label>
                            <Input type={this.state.hidden ? "password" : "text"} required="required" name="password" value={this.state.password} placeholder="Kata Sandi Anda" onChange={this.handleChange}
                            />
                            <Label check sm={12}>
                                <Col sm={12}>
                                    <Input type="checkbox" checked={this.state.check} onChange={this.handleChecked} />lihat kata sandi
                </Col>
                            </Label>
                        </FormGroup>

                        <div align="center">
                            <Button  onClick={this.onSubmit} color="submit" className="btn-primary">MASUK</Button>{" "}
                        </div>
                    </Form>
                </div>

            </div>
        );
    }
}


export default LoginPage;
