import React, {Component} from 'react';
import AppHeader from '../common/AppHeader';
import {
    Card, CardImg, CardTitle, Container, Row, 
    CardSubtitle, CardBody,
} from 'reactstrap';
import axios from 'axios';
//import {Link} from 'react-router-dom'

class InvestasiAnda extends Component {
    constructor(props) {
        super(props);
        this.state = {

            items: [
{posts:"",}

            ]

        };
    }
    componentDidMount() {
        axios.get(`https://historic-acadia-33199.herokuapp.com/api/user/investor/${localStorage.getItem('USER_ID')}` ,
        {
            headers: {
                Authorization: localStorage.getItem('JWT_TOKEN')
              }

        }
        )
            .then(res => {
                this.setState({
                    items: res.data.result
                })
            });
            posts = this.state.item.posts
            axios.get(`https://historic-acadia-33199.herokuapp.com/api/komoditas/data/${posts}`
            )
                .then(res => {
                   this.setState({
                        items: res.data.data
                    })
                    console.log(res , "sgs")
                });
    }

    render() {
        console.log('ini data', this.state.items)


        const cardData = this.state.items.map((item) => {
            return (
                <div class= 'col-md-4'>


                        <Card >
                            <CardImg top width="100%" height="200px" src={item.posts.foto} alt="Card image cap" />

                           <CardBody>
                                <CardTitle>{item.posts.nama_ternak}</CardTitle>
                                <CardSubtitle>Jumlah Lot: {item.jumlah_lot}</CardSubtitle>
                                <CardSubtitle>Status: {item.status}</CardSubtitle>
                                <CardSubtitle>Email Konfirmasi: {item.email_konfirmasi}</CardSubtitle>


                            </CardBody>


                        </Card>


                </div>
            );
        })
        return (
            <div>
                            <AppHeader />

                <Container>
                    <h1>Investasi Anda</h1>

                        <Row>
                            {cardData}
                        </Row>

                </Container>
            </div >
        );
    }
}

export default InvestasiAnda;
