import React, { Component } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';



class HomeCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      activeIndex: 0,
      items: [
         [
          {
            id: 1,
            src: 'https://www.foxsportsasia.com/uploads/2019/07/4-3-1.jpg',
            altText: 'Slide 1',
            caption: 'Slide 1'
          },
          {
            id: 2,
            src: 'https://cdn.images.dailystar.co.uk/dynamic/58/photos/451000/620x/Maurizio-Sarri-731688.jpg',
            altText: 'Slide 2',
            caption: 'Slide 2'
          },
          {
            id: 3,
            src: 'https://images.performgroup.com/di/library/GOAL/a8/17/kylian-mbappe-paris-sg-monaco-ligue-1-21042019_ehf8dejkd8jn18vh8yfu9142c.jpg?t=1096386634&quality=100',
            altText: 'Slide 3',
            caption: 'Slide 3'
          }
        ]
      ]
    };
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.goToIndex = this.goToIndex.bind(this);
    this.onExiting = this.onExiting.bind(this);
    this.onExited = this.onExited.bind(this);
  }

  onExiting() {
    this.animating = true;
  }

  onExited() {
    this.animating = false;
  }

  next() {
    if (this.animating) return;

    const nextIndex = this.state.activeIndex === items.length - 1 ? 0 : this.state.activeIndex + 1;
    this.setState({ activeIndex: nextIndex });
  }

  previous() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === 0 ? items.length - 1 : this.state.activeIndex - 1;
    this.setState({ activeIndex: nextIndex });
  }

  goToIndex(newIndex) {
    if (this.animating) return;
    this.setState({ activeIndex: newIndex });
  }

  render() {
    const { activeIndex } = this.state;

    const slides = items.map((item) => {
      return (
        <CarouselItem
          onExiting={this.onExiting}
          onExited={this.onExited}
          key={item.src}
        >
          <img src={item.src} alt={item.altText} />
          <CarouselCaption captionText={item.caption} captionHeader={item.caption} />
        </CarouselItem>
      );
    });

    return (
      <Carousel
        activeIndex={activeIndex}
        next={this.next}
        previous={this.previous}
      >
        <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={this.goToIndex} />
        {slides}
        <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
      </Carousel>
    );
  }
}


export default HomeCarousel;
