import React, { Component } from 'react';
import {
    Card, Button, CardImg, CardTitle, CardText, Container,  Row,
    CardSubtitle, CardBody
} from 'reactstrap';
import axios from 'axios';
import {Link} from 'react-router-dom'
//import { Col } from 'antd';

class HomeCard extends Component {
    constructor(props) {
        super(props);
        this.state = {

            items: [


            ]

        };
    }
    componentDidMount() {
        axios.get('https://historic-acadia-33199.herokuapp.com/api/komoditas/data'
        )
            .then(res => {
                this.setState({
                    items: res.data.result
                })
            });
    }
    render() {
        console.log('ini data', this.state.items)


        const cards = this.state.items.map((item) => {
            return (
                <div class= 'col-md-4'>


                        <Card >
                            <CardImg top width="100%" height="200px" src={item.foto} alt="Card image cap" />
                            <CardBody>
                                <CardTitle>{item.nama_ternak}</CardTitle>
                                <CardSubtitle>Harga/Lot: Rp. {item.harga}</CardSubtitle>
                                <CardText>Asal: {item.asal}</CardText>
                                <Button color="info" ><Link  to={`/investasi/${item._id}`}  >Invest</Link></Button>
                            </CardBody>

                        </Card>


                </div>
            );
        })
        return (
            <div>
                <Container>

                        <Row>
                            {cards}
                        </Row>

                </Container>
                <br/>
            </div >
        );
    }
}

export default HomeCard;
