import React, { Component } from 'react';
import {
    Card, Button, CardImg, CardTitle, CardText, Container, Row, Col,
    CardSubtitle, CardBody
} from 'reactstrap';
import axios from 'axios';
class HomeCard extends Component {
    constructor(props) {
        super(props);
        this.state = {

            items: [


            ]

        };
    }
    componentDidMount() {
        axios.get('http://reduxblog.herokuapp.com/api/posts?key=tester'
        )
            .then(res => {
                this.setState({
                    items: res.data
                })
            });
    }
    render() {
        console.log('ini data', this.state.items)


        const cards = this.state.items.map((item) => {
            return (
                <div>



                        <Card >
                        < Col md='4'>
                            <CardImg top width="100%" src={item.categories} alt="Card image cap" />
                            <CardBody>
                                <CardTitle>{item.title}</CardTitle>
                                <CardSubtitle>{item.content}</CardSubtitle>
                                <CardText>This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</CardText>
                                <Button>Button</Button>
                            </CardBody>
                            </Col>
                        </Card>


                </div>
            );
        })
        return (
            <div>
                <Container>

                        <Row>
                            {cards}
                        </Row>

                </Container>
            </div >
        );
    }
}

export default HomeCard;
