import React, {Component} from 'react';
import AppHeader from '../common/AppHeader';
import {
    Card, Button, CardImg, CardTitle, Container, Row, 
    CardSubtitle, CardBody,
} from 'reactstrap';
import axios from 'axios';
import {Link} from 'react-router-dom'

class KomoditasAnda extends Component {
    constructor(props) {
        super(props);
        this.state = {

            items: [


            ]

        };
    }
    componentDidMount() {
        axios.get(`https://historic-acadia-33199.herokuapp.com/api/user/peternak/${localStorage.getItem('USER_ID')}` ,
        {
            headers: {
                Authorization: localStorage.getItem('JWT_TOKEN')
              }

        }
        )
            .then(res => {
                this.setState({
                    items: res.data.data
                })
            });
    }
    render() {
        console.log('ini data', this.state.items)


        const cards = this.state.items.map((item) => {
            return (
                <div class= 'col-md-4'>


                        <Card >
                            <CardImg top width="100%" height="200px" src={item.foto} alt="Card image cap" />

                           <CardBody>
                                <CardTitle>{item.nama_ternak}</CardTitle>
                                <CardSubtitle>Harga:        {item.harga}</CardSubtitle>
                                <CardSubtitle>Asal:         {item.asal}</CardSubtitle>
                                <CardSubtitle>Lot Tersedia: {item.lot_tersedia}</CardSubtitle>

                                <Button color="danger"><Link to={`/investasi/${item._id}`}>Lihat Investor</Link></Button>

                            </CardBody>


                        </Card>


                </div>
            );
        })
        return (
            <div>
                            <AppHeader />

                <Container>
                    <h1>Komoditas Anda</h1>

                        <Row>
                            {cards}
                        </Row>

                </Container>
            </div >
        );
    }
}

export default KomoditasAnda;
