import React, { Component } from 'react';
import {
    Card, CardImg, CardTitle, CardText,
    CardSubtitle, CardBody,
} from 'reactstrap';
import axios from 'axios';
import AppHeader from '../common/AppHeader';
import isLoggedIn from '../helpers/is_logged_in';
import { Slider, Handles } from 'react-compound-slider'
import { Redirect } from 'react-router-dom';



import Detail from './Detail';

class DetailPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            handle: { id, value, percent }, 
  getHandleProps,
            items: [


            ]



        };
        this.handleid = this.handleid.bind(this)
        this.handlevalue = this.handlevalue.bind(this)
        this.handlepercent = this.handpercent.bind(this)
        this.handlegetHandleProps = this.handlegetHandleProps.bind(this)
    }
    handleHarga(event) {
        this.setState({
            harga: event.target.value
        })
    }
    handleNama_ternak(event) {
        this.setState({
            nama_ternak: event.target.value
        })
    }
    handleLot_tersedia(event) {
        this.setState({
            lot_tersedia: event.target.value
        })
    }
    componentDidMount() {
        axios.get(`https://historic-acadia-33199.herokuapp.com/api/komoditas/data/${this.props.match.params.id}`
        )
            .then(res => {
                this.setState({
                    items: res.data.data
                })
                console.log(res, "sgs")
            });
    }
    render() {
        if (!isLoggedIn() || localStorage.getItem('Roles') === 'Peternak') {
            return (alert("Maaf peternak tidak dapat melakukan investasi"), <Redirect to='/' />)
        }
        console.log(this.props.match.params.id)
        const { foto, nama_ternak, harga, lot_tersedia, asal } = this.state.items

        const sliderStyle = {  // Give the slider some width
            position: 'relative',
            width: '100%',
            height: 80,
            border: '1px solid steelblue',
        }

        const railStyle = {
            position: 'absolute',
            width: '100%',
            height: 10,
            marginTop: 35,
            borderRadius: 5,
            backgroundColor: '#8B9CB6',
        }
        return (

            <div>
                <AppHeader />
                <Detail />
                <Card >
                    <CardImg top width="100%" height="500px" src={foto} alt="Card image cap" />

                    <CardBody>
                        <CardTitle>{nama_ternak}</CardTitle>
                        <CardSubtitle>{harga}</CardSubtitle>
                        <CardText>Asal: {asal} <br></br>Jumlah Lot: {lot_tersedia}</CardText>
                        <CardText></CardText>
                    </CardBody>
                </Card>
                <div
      style={{
        left: `${percent}%`,
        position: 'absolute',
        marginLeft: -15,
        marginTop: 25,
        zIndex: 2,
        width: 30,
        height: 30,
        border: 0,
        textAlign: 'center',
        cursor: 'pointer',
        borderRadius: '50%',
        backgroundColor: '#2C4870',
        color: '#333',
      }}
      {...getHandleProps(id)}
    >
      <div style={{ fontFamily: 'Roboto', fontSize: 11, marginTop: -35 }}>
        {value}
      </div>
    </div>
  )
}

<Slider
    rootStyle={sliderStyle}
    domain={[0, 100]}
    step={1}
    mode={2}
    values={[30]}
  >
    <div style={railStyle} />
    <Handles>
      {({ handles, getHandleProps }) => (
        <div className="slider-handles">
          {handles.map(handle => (
            <Handle
              key={handle.id}
              handle={handle}
              getHandleProps={getHandleProps}
            />
          ))}
        </div>
      )}
    </Handles>
  </Slider>
            </div>
        );


    }

}

export default DetailPage;
