import React from 'react';
import Avatar from 'react-avatar';
import store from 'store'

import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  Container,
  DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom'
import axios from 'axios';
import isLoggedIn from '../helpers/is_logged_in';

export default class AppHeader extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false,
     user_foto: '',
     username: '',
     email: ''
    };
  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }
  componentDidMount() {
    axios.get(`https://historic-acadia-33199.herokuapp.com/api/user/${localStorage.getItem('USER_ID')}`
    )
        .then(res => {
            this.setState({
                user_foto: res.data.data.user_foto,
                username: res.data.data.username,
                email: res.data.data.email

            })

            console.log(this.state.username, "OK")
        });
}
logOut() {
  store.remove('loggedIn');
  localStorage.removeItem('JWT_TOKEN')
  localStorage.removeItem('USER_ID')
  this.props.history.push('/')
}
  render() {
    let loginicon
    let logInOut


    if (isLoggedIn())  {
      logInOut = <NavLink  ><Link onClick={this.logOut} className='text-white' >Logout</Link></NavLink>

      loginicon = 
      <div>
        <Navbar color="danger" light expand="md">
            <Container >
          <NavbarBrand ><Link to='/'className="text-white"><img height='40px' src='https://monggovest.herokuapp.com/static/img/Logo-White@2x.39103d4.png' alt=''/></Link></NavbarBrand>
          <NavbarToggler onClick={this.toggle} />
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav className="ml-auto" navbar>
              <NavItem>
                <NavLink ><Link to='/investasi'className="text-white">Investasi</Link></NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
              </NavItem>
              <UncontrolledDropdown nav inNavbar>
              <DropdownToggle nav caret ><Avatar size="25" round={true} src={this.state.user_foto}></Avatar> {this.state.username}</DropdownToggle>
                <DropdownMenu right>
                  <DropdownItem>
                    Option 1
                  </DropdownItem>
                  <DropdownItem>
                    Option 2
                  </DropdownItem>
                  <DropdownItem divider />
                  <DropdownItem>
                    Reset
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>

            </Nav>
          </Collapse>
          </Container >
        </Navbar>
      </div>

  } else {logInOut = <NavLink  ><Link className='text-white' to='/login' >Login</Link></NavLink>}
    return (
      <div>
        <Navbar color="danger" light expand="md">
            <Container >
          <NavbarBrand ><Link to='/'className="text-white"><img height='40px' src='https://monggovest.herokuapp.com/static/img/Logo-White@2x.39103d4.png' alt=''/></Link></NavbarBrand>
          <NavbarToggler onClick={this.toggle} />
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav className="ml-auto" navbar>
              <NavItem>
                <NavLink ><Link to='/investasi'className="text-white">Investasi</Link></NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
              </NavItem>
              <UncontrolledDropdown nav inNavbar>
                  {loginicon}
                <DropdownMenu right>
                  <DropdownItem>
                    Option 1
                  </DropdownItem>
                  <DropdownItem>
                    Option 2
                  </DropdownItem>
                  <DropdownItem divider />
                  <DropdownItem>
                    Reset
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
      {logInOut}
            </Nav>
          </Collapse>
          </Container >
        </Navbar>
      </div>
    );
  }
}
