import React, { Component } from 'react';
import {
    Card,  CardImg, CardTitle, CardText, 
    CardSubtitle, CardBody, Container, Button, Input
} from 'reactstrap';
import axios from 'axios';
import AppHeader from '../common/AppHeader';
import isLoggedIn from '../helpers/is_logged_in';
import { Redirect } from 'react-router-dom';
import Handle from '../detail/slider'
import { Slider, Rail, Handles, Tracks } from 'react-compound-slider'
import Track from '../detail/sliderTrack'


import Detail from './Detail';

class DetailPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            jumlah_lot: "",
            items: [


            ]



        };
        this.postLot = this.postLot.bind(this)
        this.changeLot = this.changeLot.bind(this)
    }

    changeLot(event) {
      this.setState({
          jumlah_lot: event.target.value
      })
  }
    componentDidMount() {
        axios.get(`https://historic-acadia-33199.herokuapp.com/api/komoditas/data/${this.props.match.params.id}`
        )
            .then(res => {
               this.setState({
                    items: res.data.data
                })
                console.log(res , "sgs")
            });
    }
    postLot(event) {
      event.preventDefault();
      // if (this.state.asal === this.state.confirmasal) {

      axios
          .post(`https://historic-acadia-33199.herokuapp.com/api/user/invest/lot/${this.props.match.params.id}`, 

          {
              jumlah_lot: this.state.jumlah_lot
          },
              {
                  headers: {
                      Authorization: localStorage.getItem('JWT_TOKEN')
                    }

              }

        )
          .then((res) => {
              console.log(res, "the res");
              alert('jumlah lot anda tersimpan');
              this.props.history.push('/')

          })
          .catch(function (error) {
              console.log("the error", error);
              alert("error", error);
          });
  }



    render() 

     {

        if (!isLoggedIn () || localStorage.getItem('Roles') === 'Peternak' ) {
            return (alert("Maaf peternak tidak dapat melakukan investasi") , <Redirect to='/' /> ) 
         } 
        console.log(this.props.match.params.id)
        console.log(this.state.jumlah_lot, "tess")
        const {foto, nama_ternak, harga, lot_tersedia, asal, jumlah_lot} = this.state.items

        const sliderStyle = {  // Give the slider some width
            position: 'relative',
            width: '25%',
            height: 80,

          }

          const railStyle = { 
            position: 'absolute',
            width: '100%',
            height: 10,
            marginTop: 35,
            borderRadius: 5,
            backgroundColor: '#8B9CB6'
          }
        return (

            <div>
<AppHeader />
                <Detail />
                <Container>
                <Card >
                            <CardImg top width="100%" height="500px" src={foto} alt="Card image cap" />

                           <CardBody>
                                <CardTitle>{nama_ternak}</CardTitle>
                                <CardSubtitle>{harga}</CardSubtitle>
                                <CardText>Asal: {asal} <br></br>Lot yang tersedia: {lot_tersedia}</CardText>
                                <CardTitle>Jumlah Lot yang diinginkan:</CardTitle>

                                <Input value= {jumlah_lot} onChange={this.changeLot} type={<Slider  
    rootStyle={sliderStyle}
    domain={[0, 20]}
    step={1}
    mode={2}
    values={[0]}
  >
    <Rail value= {jumlah_lot} onChange={this.changeLot}>
      {({ getRailProps }) => (  // adding the rail props sets up events on the rail
        <div style={railStyle} {...getRailProps()} /> 
      )}
    </Rail>
    <Handles>
      {({ handles, getHandleProps }) => (
        <div className="slider-handles">
          {handles.map(handle => (
            <Handle
              key={handle.id}
              handle={handle}
              getHandleProps={getHandleProps}
            />
          ))}
        </div>
      )}
    </Handles> 
    <Tracks  right={false}>
      {({ tracks, getTrackProps }) => (
        <div className="slider-tracks">
          {tracks.map(({ id, source, target }) => (
            <Track
              key={id}
              source={source}
              target={target}
              getTrackProps={getTrackProps}
            />
          ))}
        </div>
      )}
    </Tracks>
          </Slider>}></Input>
  </CardBody>

  <br></br>
  <Button onClick={this.postLot} color='submit' className="btn-primary">Submit Lot</Button>

                        </Card>
                        <Input type="range"  value= {jumlah_lot} onChange={this.changeLot} />
                        </Container>
            </div>
         ) ;


    }

}

export default DetailPage;
